const { isValidInput, processButtonClick } = require('test/script');

test('isValidInput should return true for valid input', () => {
    const validInput = 'John Doe';
    expect(isValidInput(validInput)).toBe(true);
});

test('isValidInput should return false for invalid input', () => {
    const invalidInput = '123';
    expect(isValidInput(invalidInput)).toBe(false);
});

test('processButtonClick should update outputText and lastWords for valid input', () => {
    const validInput = 'John Doe';
    const mockInputText = { value: validInput };
    const mockOutputText = { textContent: '' };
    const mockLastWords = [];
    global.inputText = mockInputText;
    global.outputText = mockOutputText;
    global.lastWords = mockLastWords;
    processButtonClick();
    expect(mockOutputText.textContent).toBe(validInput);
    expect(mockLastWords.length).toBe(1);
    expect(mockLastWords[0]).toBe(validInput);
});
