const { isValidName } = require('./script');

test('isValidName should return true for valid names', () => {
  const validNames = ['John Doe', 'Alice', 'Bob White'];
  validNames.forEach(name => {
    expect(isValidName(name)).toBe(true);
  });
});

test('isValidName should return false for invalid names', () => {
  const invalidNames = ['123', 'John123', '!@#', 'John Doe1'];
  invalidNames.forEach(name => {
    expect(isValidName(name)).toBe(false);
  });
});