const { updateWordList } = require('./script');

test('updateWordList should update the word list in the HTML', () => {
  document.body.innerHTML = '<div id="wordList"></div>';
  const mockLastWords = ['John', 'Doe', 'Alice'];
  updateWordList(mockLastWords);
  const wordListElement = document.getElementById('wordList');
  const listItems = wordListElement.querySelectorAll('li');
  expect(listItems.length).toBe(mockLastWords.length);
  listItems.forEach((item, index) => {
    expect(item.textContent).toBe(mockLastWords[index]);
  });
});
